sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	gs.info(current.checklist);
	var checkListItem = new GlideRecord('checklist_item');
	checkListItem.addQuery('checklist','=',current.checklist);
	checkListItem.addQuery('complete','=','false');
	checkListItem.query();
// 	var completeAll = true;
// 	gs.info(checkListItem.checklist);
// 	while(checkListItem.next()) {
// 		gs.info(checkListItem.complete);
// 		if (checkListItem.complete == 'false'){
// 			completeAll = false;
// 		}
// 	}
	gs.info('RowCount='+ checkListItem.getRowCount());
	if (checkListItem.getRowCount() == 0) {
		var checkList = new GlideRecord('checklist');
		checkList.addQuery('sys_id','=',current.checklist);
		checkList.query();
		while (checkList.next()) {
			var task = new GlideRecord('x_58872_needit_needit_task');
			task.addQuery('sys_id','=',checkList.document);
			task.query();
			while(task.next()) {
				task.state = 3;
				task.update();
			}
		}
	}

})(current, previous);
